$IP = "172.16.0.21"$MaskBits = 24 # This means subnet mask = 255.255.255.0$Gateway = "172.16.0.1"$Dns = "172.16.0.10"$IPType = "IPv4"$Adapter = Get-NetAdapter -Name London_NetworkIf (($Adapter | Get-NetIPConfiguration).IPv4Address.IPAddress) {    $Adapter | Remove-NetIPAddress -AddressFamily $IPType -Confirm:$false}If (($Adapter | Get-NetIPConfiguration).Ipv4DefaultGateway) {    $Adapter | Remove-NetRoute -AddressFamily $IPType -Confirm:$false}# Configure the IP address and default gateway$Adapter | New-NetIPAddress `    -AddressFamily $IPType `    -IPAddress $IP `    -PrefixLength $MaskBits `    -DefaultGateway $Gateway# Configure the DNS client server IP addresses$Adapter | Set-DnsClientServerAddress -ServerAddresses $Dns