$IP = "192.168.16.34"$MaskBits = 24 # This means subnet mask = 255.255.255.0$Gateway = "192.168.16.1"$Dns = "192.168.16.11"$IPType = "IPv4"$Adapter = Get-NetAdapterIf (($Adapter | Get-NetIPConfiguration).IPv4Address.IPAddress) {    $Adapter | Remove-NetIPAddress -AddressFamily $IPType -Confirm:$false}If (($Adapter | Get-NetIPConfiguration).Ipv4DefaultGateway) {    $Adapter | Remove-NetRoute -AddressFamily $IPType -Confirm:$false}# Configure the IP address and default gateway$Adapter | New-NetIPAddress `    -AddressFamily $IPType `    -IPAddress $IP `    -PrefixLength $MaskBits `    -DefaultGateway $Gateway# Configure the DNS client server IP addresses$Adapter | Set-DnsClientServerAddress -ServerAddresses $Dns